import React ,{useState}from 'react'
import {StyleSheet,View,Text,Button} from 'react-native'
import { RadioButton } from 'react-native-paper';
import SecondScreen from "./SecondScreen"

const HomeScreen=({navigation})=>{
    const [checked, setChecked]=React.useState('tekbasima')
    return <View>
        <Text style={styles.text}>Tatile kimlerle gitmeyi planlıyorsun ?</Text>
        <Text >Tek Başima</Text>
        <RadioButton 
        value="tekbasima"
        status={checked==="tekbasima" ? "checked" : "unchecked"}
        onPress={()=>{setChecked("tekbasima")}}
        ></RadioButton>
        <Text >Ailemle</Text>
        <RadioButton 
        value="ailemle"
        status={checked==="ailemle" ? "checked" : "unchecked"}
        onPress={()=>{setChecked("ailemle")}}
        ></RadioButton>
        <Text >Arkadaşlarimla</Text>
        <RadioButton 
        value="arkadaslarimla"
        status={checked==="arkadaslarimla" ? "checked" : "unchecked"}
        onPress={()=>{setChecked("arkdaslarimla")}}
        ></RadioButton>
        <Text >Eşimle,Sevgilimle</Text>
        <RadioButton 
        value="partnerimle"
        status={checked==="partnerimle" ? "checked" : "unchecked"}
        onPress={()=>{setChecked("partnerimle")}}
        ></RadioButton>
      <Button title="İleri" onPress={()=>navigation.navigate('SecondScreen')}/>
    </View>
}

const styles = StyleSheet.create({
  text: {
    fontSize: 30
  }
});

export default HomeScreen
